import * as THREE from 'three'
import { useRef, useState, useEffect, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Image, Environment, ScrollControls, useScroll, useTexture, useGLTF, Html } from '@react-three/drei'
import { easing } from 'maath'
import './util'
import './App.css'

function Loader({ onEnterSite }) {
  const { active, progress, errors, item, loaded, total } = useProgress();
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    if (progress === 100) {
      setTimeout(() => {
        setShowButton(true);
      }, 500);
    }
  }, [progress]);

  const handleEnterSite = () => {
    const loaderContainer = document.querySelector(".loader-container");
    loaderContainer.classList.add("fade-out");
    setTimeout(onEnterSite, 1000);
  };

  return (
    <Html center>
      <div
        className="loader-container"
        style={{
          position: "fixed",
          backgroundColor: "white",
          transform: "translate(-50%, -50%)",
          textAlign: "center",
          color: "black",
          width: "100vw",
          height: "100vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          border: "1px solid red",
        }}
      >
        <h1 className="company-name">MASS ENERGY STORAGE SYSTEMS</h1>
        <div className="battery-container">
          <div className="battery-body">
            <div
              className="battery-fill"
              style={{
                width: `${Math.round(progress)}%`,
                backgroundColor: "#4CAF50",
                transition: "width 0.5s ease-in-out",
              }}
            ></div>
          </div>
          <div className="battery-cap"></div>
        </div>
        <div className="progress-text">
          {progress < 100 ? (
            <div className="progress-text-value">{`${Math.round(
              progress
            )}% Charging`}</div>
          ) : (
            <div className="progress-text-value">Charging Done!</div>
          )}
        </div>
        {showButton && (
          <button
            className="enter-button"
            style={{
              marginTop: "20px",
            }}
            onClick={handleEnterSite}
          >
            Enter Site
          </button>
        )}
      </div>
    </Html>
  );
}



export const App = () => {
  const [fov, setFov] = useState(15)
  const [showScrollText, setShowScrollText] = useState(true)

  const toggleFov = () => {
    setFov(prevFov => (prevFov === 15 ? 105 : 15))
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowScrollText(false)
    }, 7000) // Hide the text after 5 seconds

    return () => clearTimeout(timer)
  }, [])

  return (
    <div className="app-container">
      <Canvas camera={{ position: [0, 0, 100], fov }}>
        <Suspense fallback={<CubeLoader/>}>
        <fog attach="fog" args={['#a79', 8.5, 12]} />
        <ScrollControls pages={4} infinite>
          <Rig rotation={[0, 0, 0.15]} fov={fov}>
            <Carousel />
          </Rig>
          <Model position={[0, -0.15, 0]} /> {/* Replace the mesh with the model */}
          <Banner position={[0, -0.15, 0]} />
        </ScrollControls>
        <Environment files='./assets/sky.exr' background />
        {showScrollText && (
          <Html>
            <div className="scroll-instruction">
              Scroll to explore
            </div>
          </Html>
        )}
        </Suspense>
      </Canvas>

      <button 
        onClick={toggleFov}
        className='toggle-button press-start-2p-regular'
      >
        To the Moon
      </button>
    </div>
  )
}

function Rig({ fov, ...props }) {
  const ref = useRef()
  const scroll = useScroll()
  useFrame((state, delta) => {
    ref.current.rotation.y = -scroll.offset * (Math.PI * 2) // Rotate contents
    state.events.update() // Raycasts every frame rather than on pointer-move
    easing.damp3(state.camera.position, [-state.pointer.x * 2, state.pointer.y + 1.5, 10], 0.3, delta) // Move camera
    state.camera.lookAt(0, 0, 0) // Look at center
    state.camera.fov = THREE.MathUtils.lerp(state.camera.fov, fov, 0.1) // Animate FOV
    state.camera.updateProjectionMatrix() // Update projection matrix
  })
  return <group ref={ref} {...props} />
}

function Carousel({ radius = 1.4, count = 8 }) {
  return Array.from({ length: count }, (_, i) => (
    <Card
      key={i}
      url={`/img${Math.floor(i % 10) + 1}_.webp`}
      position={[Math.sin((i / count) * Math.PI * 2) * radius, 0, Math.cos((i / count) * Math.PI * 2) * radius]}
      rotation={[0, Math.PI + (i / count) * Math.PI * 2, 0]}
    />
  ))
}

function Card({ url, ...props }) {
  const ref = useRef()
  const [hovered, hover] = useState(false)
  const pointerOver = (e) => (e.stopPropagation(), hover(true))
  const pointerOut = () => hover(false)
  useFrame((state, delta) => {
    easing.damp3(ref.current.scale, hovered ? 1.15 : 1, 0.1, delta)
    easing.damp(ref.current.material, 'radius', hovered ? 0.25 : 0.1, 0.2, delta)
    easing.damp(ref.current.material, 'zoom', hovered ? 1 : 1.5, 0.2, delta)
  })
  return (
    <Image ref={ref} url={url} transparent side={THREE.DoubleSide} onPointerOver={pointerOver} onPointerOut={pointerOut} {...props}>
      <bentPlaneGeometry args={[0.1, 1, 1, 20, 20]} />
    </Image>
  )
}

function Model(props) {
  const { nodes, materials } = useGLTF('/binance.glb')
  const btc = useRef()
  useFrame((state, delta) => {
    btc.current.rotation.y += delta / .9
  })
  return (
    <group ref={btc} scale={2} {...props} dispose={null}>
      <mesh castShadow receiveShadow geometry={nodes.mesh.geometry} material={materials.base_material} rotation={[Math.PI / 2, 0, 1.5]} scale={1.707} />
    </group>
  )
}

function Banner(props) {
  const ref = useRef()
  const texture = useTexture('/work_.png')
  texture.wrapS = texture.wrapT = THREE.RepeatWrapping
  const scroll = useScroll()
  useFrame((state, delta) => {
    ref.current.material.time.value += Math.abs(scroll.delta) * 4
    ref.current.material.map.offset.x += delta / 2
  })
  return (
    <mesh ref={ref} {...props}>
      <cylinderGeometry args={[1.6, 1.6, 0.14, 128, 16, true]} />
      <meshSineMaterial map={texture} map-anisotropy={16} map-repeat={[30, 1]} side={THREE.DoubleSide} toneMapped={false} />
    </mesh>
  )
}
