import * as THREE from 'three'
import { useEffect, useState } from 'react'
import { extend, useThree } from '@react-three/fiber'

// Paul West @prisoner849 https://discourse.threejs.org/u/prisoner849
// https://discourse.threejs.org/t/simple-curved-plane/26647/10
class BentPlaneGeometry extends THREE.PlaneGeometry {
  constructor(radius, ...args) {
    super(...args)
    let p = this.parameters
    let hw = p.width * 0.5
    let a = new THREE.Vector2(-hw, 0)
    let b = new THREE.Vector2(0, radius)
    // Change this to make the picture further out
    let c = new THREE.Vector2(hw, 0)
    let ab = new THREE.Vector2().subVectors(a, b)
    let bc = new THREE.Vector2().subVectors(b, c)
    let ac = new THREE.Vector2().subVectors(a, c)
    let r = (ab.length() * bc.length() * ac.length()) / (2 * Math.abs(ab.cross(ac)))
    let center = new THREE.Vector2(0, radius - r)
    let baseV = new THREE.Vector2().subVectors(a, center)
    let baseAngle = baseV.angle() - Math.PI * 0.5
    let arc = baseAngle * 2
    let uv = this.attributes.uv
    let pos = this.attributes.position
    let mainV = new THREE.Vector2()
    for (let i = 0; i < uv.count; i++) {
      let uvRatio = 1 - uv.getX(i)
      let y = pos.getY(i)
      mainV.copy(c).rotateAround(center, arc * uvRatio)
      pos.setXYZ(i, mainV.x, y, -mainV.y)
    }
    pos.needsUpdate = true
  }
}

class MeshSineMaterial extends THREE.MeshBasicMaterial {
  constructor(parameters = {}) {
    super(parameters)
    this.setValues(parameters)
    this.time = { value: 0 }
  }
  onBeforeCompile(shader) {
    shader.uniforms.time = this.time
    shader.vertexShader = `
      uniform float time;
      ${shader.vertexShader}
    `
    shader.vertexShader = shader.vertexShader.replace(
      '#include <begin_vertex>',
      `vec3 transformed = vec3(position.x, position.y + sin(time + uv.x * PI * 4.0) / 4.0, position.z);`
    )
  }
}

extend({ MeshSineMaterial, BentPlaneGeometry })

export const App = () => {
  const [wheelSize, setWheelSize] = useState(1.6)

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 480) {
        setWheelSize(1.0) // Smaller wheel size for small screens
      } else if (window.innerWidth < 768) {
        setWheelSize(1.3) // Medium wheel size for medium screens
      } else {
        setWheelSize(1.6) // Default wheel size for large screens
      }
    }

    window.addEventListener('resize', handleResize)
    handleResize() // Call once to set initial size

    return () => window.removeEventListener('resize', handleResize)
  }, [])

  return (
    <div className="app-container">
      <Canvas camera={{ position: [0, 0, 100], fov: 15 }}>
        <Suspense fallback={<CubeLoader setLoadingComplete={setLoadingComplete} />}>
          <fog attach="fog" args={['#a79', 8.5, 12]} />
          <ScrollControls pages={4} infinite>
            <Rig rotation={[0, 0, 0.15]} fov={15}>
              <Carousel wheelSize={wheelSize} />
            </Rig>
            <Model position={[0, -0.15, 0]} /> {/* Replace the mesh with the model */}
            <Banner position={[0, -0.15, 0]} wheelSize={wheelSize} />
          </ScrollControls>
          <Environment files="./assets/sky.exr" background />
          {showScrollText && (
            <Html>
              <div className="scroll-instruction">Scroll to explore</div>
            </Html>
          )}
        </Suspense>
      </Canvas>

      {loadingComplete && (
        <button onClick={toggleFov} className="toggle-button press-start-2p-regular">
          To the Moon
        </button>
      )}
    </div>
  )
}

function Banner({ wheelSize, ...props }) {
  const ref = useRef()
  const texture = useTexture('/work_.png')
  texture.wrapS = texture.wrapT = THREE.RepeatWrapping
  const scroll = useScroll()
  useFrame((state, delta) => {
    ref.current.material.time.value += Math.abs(scroll.delta) * 4
    ref.current.material.map.offset.x += delta / 2
  })
  return (
    <mesh ref={ref} {...props}>
      <cylinderGeometry args={[wheelSize, wheelSize, 0.14, 128, 16, true]} />
      <meshSineMaterial map={texture} map-anisotropy={16} map-repeat={[30, 1]} side={THREE.DoubleSide} toneMapped={false} />
    </mesh>
  )
}
